import React, { useState } from 'react';
import { useRouter } from 'next/router';
import styles from './Header.modules.scss';

const Header = () => {
  const router = useRouter();
  const [searchInput, setSearchInput] = useState("");

  const handleInputChange = ({ target }) => {
    setSearchInput(target.value);
  };

  const onSubmitHamdler = e => {
    e.preventDefault();
    router.replace(`/photos/${searchInput}`);
  };
  return (
    <div className={styles.header}>
      <form onSubmit={onSubmitHamdler}>
        <input onChange={handleInputChange} value={searchInput} className={styles.searchInput} placeholder="Search free high-resolution photo"/>
      </form>
    </div>
  );
};

export default Header;
