const SET_PHOTOS = '@@PHOTOS/SET_PHOTOS';

export const initialState = {
  photos: null
}

export const photosReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_PHOTOS:
      return {
        ...state,
        photos: action.payload
      }
    default:
      return state
  }
}

export const setPhotos = (photos) => ({
  type: SET_PHOTOS,
  payload: photos
})

export const searchPhotos = (text) => (dispatch) => {
  dispatch(setPhotos(text))
}
